import "./ProductPage.css";
import { useState } from "react";
import Image from "react-bootstrap/Image";

import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";

const ServerProductPage = ({ item }) => {
    const [validated, setValidated] = useState(false);
    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');
    const [email, setEmail] = useState('');
    const [phone, setPhone] = useState('');
    const [address1, setAddress1] = useState('');
    const [address2, setAddress2] = useState('');
    const [city, setCity] = useState('');
    const [state, setState] = useState('');
    const [zip, setZip] = useState('');
    const [card, setCard] = useState('');
    const [shipping, setShipping] = useState('');


    const handleSubmit = (event) => {
        const form = event.currentTarget;
        if (form.checkValidity() === false) {
            event.preventDefault();
            event.stopPropagation();
        }

        setValidated(true);

        const emailSubject = 'New Order';
        const emailBody = `

        ----- Order Confirmation -----
        Thank you for ordering ${item.name}.
        Your subtotal: ${item.price}

        Name: ${firstName} ${lastName}\n
        Email: ${email}\n
        Phone: ${phone}\n
        Address: ${address1} ${address2}\n
        ${city}, ${state} ${zip}\n\n

        Card: ${card.substring(-4)}

        Shipping Method: ${shipping}

        `;
        const mailToLink = `mailto:sales@example.com?subject=${encodeURIComponent(emailSubject)}&body=${encodeURIComponent(emailBody)}`;

        window.location.href = mailToLink;
    };

    return (
        <div className="ServerProductPage">
            <Image className="image" src={item.imageURL} />
            <div className="description">
                <h1>{item.name}</h1>
                <h5>{item.subtitle}</h5>
                <br />
                <h2>${item.price}</h2>
                <h6>{item.reviews} reviews</h6>
                <br />
                <div className="details">
                    {item.description.map((line, key) => (
                        <div key={key + Math.random()}>{line}</div>
                    ))}
                </div>

            </div>

            <div className="formContainer">
                <Form validated={validated} onSubmit={handleSubmit}>
                    <h3>Order now!</h3>
                    <Form.Label>Name</Form.Label>
                    <Row>
                        <Col>
                            <Form.Group>
                                <Form.Control required type="name" placeholder="First" onChange={setFirstName}/>
                            </Form.Group>
                        </Col>
                        <Col>
                            <Form.Group>
                                <Form.Control required type="name" placeholder="Last" onChange={setLastName}/>
                            </Form.Group>
                        </Col>
                    </Row>

                    <Form.Group>
                        <Form.Label>E-mail</Form.Label>
                        <Form.Control required type="email" placeholder="email@example.com" onChange={setEmail}/>
                    </Form.Group>

                    <Form.Group>
                        <Form.Label>Phone</Form.Label>
                        <Form.Control required type="tel" pattern="[0-9]{3}-?[0-9]{3}-?[0-9]{4}" placeholder="XXX-XXX-XXXX" onChange={setPhone}/>
                    </Form.Group>

                    <Form.Group>
                        <Form.Label>Shipping Address</Form.Label>
                        <Form.Control required type="address" placeholder="Address Line 1" onChange={setAddress1}/>
                        <Form.Control type="address" placeholder="Address Line 2" onChange={setAddress2}/>
                    </Form.Group>
                    <Row>
                        <Col>
                            <Form.Group>
                                <Form.Control required type="text" placeholder="City" onChange={setCity}/>
                            </Form.Group>
                        </Col>
                        <Col>
                            <Form.Group>
                                <Form.Control required type="text" placeholder="State" onChange={setState}/>
                            </Form.Group>
                        </Col>
                        <Col>
                            <Form.Group>
                                <Form.Control required type="text" pattern="[0-9]{5}" placeholder="Zip code" onChange={setZip}/>
                            </Form.Group>
                        </Col>
                    </Row>

                    <Form.Group>
                        <Form.Label>Card number</Form.Label>
                        <Form.Control required type="text" pattern="[0-9]{15,16}" placeholder="Card number" onChange={setCard}/>
                    </Form.Group>

                    <Form.Group>
                        <Form.Label>Security code</Form.Label>
                        <Form.Control required type="text" pattern="[0-9]{3,4}" placeholder="CVC"/>
                    </Form.Group>
                    <br />
                    <Form.Select required onChange={setShipping}>
                        <option>---Select shipping method---</option>
                        <option>Overnight</option>
                        <option>2-days expedited</option>
                        <option>6-days ground</option>
                    </Form.Select>

                    <br />
                    <Button type="submit" variant="dark" sz="lg" > Submit </Button>
                </Form>
            </div>
        </div >
    )
}

export default ServerProductPage;
