import './Header.css';
import { Container, Navbar, Nav } from 'react-bootstrap';
import { useState, useEffect } from 'react';
import axios from 'axios';

const Header = () => {
    const [cartCount, setCartCount] = useState(0);

    useEffect(() => {
        async function fetchCartCount() {
            axios.get('http://localhost:8080/cart')
                .then((response) => setCartCount(response.data.length))
                .catch((error) => console.log(error));
        }
        fetchCartCount();
    }, []);

    return (
        <Navbar collapseOnSelect expand="lg" bg="dark" variant="dark" className='Header'>
            <Container>
                <Navbar.Brand>MrBeef</Navbar.Brand>
                <Navbar.Toggle aria-controls="basic-navbar-nav" />
                <Navbar.Collapse id="basic-navbar-nav">
                    <Nav>
                        <Nav.Link href="/">Home</Nav.Link>
                        <Nav.Link href="/products">Products</Nav.Link>
                        <Nav.Link href="/checkout">Check Out</Nav.Link>
                        <Nav.Link href="/starrating">Star Rating</Nav.Link>
                    </Nav>
                    <Nav>
                        <Nav.Link href="/cart" className='justify-content-end'>Shopping Cart ({cartCount})</Nav.Link>
                    </Nav>
                </Navbar.Collapse>
            </Container>
        </Navbar>
    )
}

export default Header;
